SELECT a.*, b.mainImg, a.deldate, ( SELECT COUNT(*) FROM `product` AS a JOIN `productimg` AS b ON a.pno = b.pno JOIN `seller` AS c ON a.sid = c.uid
a.deldate IS NULL AND a.pname like CONCAT('%','${keyword}','%') AND a.company like CONCAT('%','${keyword}','%') AND a.sid like CONCAT('%','${keyword}','%') AND a.pno=${keyword}
) AS line FROM `product` AS a JOIN `productimg` AS b ON a.pno = b.pno JOIN `seller` AS c ON a.sid = c.uid
a.deldate IS NULL AND a.pname like CONCAT('%','${keyword}','%') AND a.company like CONCAT('%','${keyword}','%') AND a.sid like CONCAT('%','${keyword}','%') AND a.pno=${keyword}
ORDER BY a.rdate DESC, a.pno ASC LIMIT ${size} OFFSET ${(pg - 1) * size}

SELECT o.ono, o.odate, o.total, o.uid, COUNT(od.ono) AS `count`, ANY_VALUE(p.cate) AS cate, ANY_VALUE(ip.mainimg) AS mainimg, ANY_VALUE(p.pname) AS pname, ( SELECT COUNT(DISTINCT o.ono) FROM `orders` AS o JOIN `orderdetail` AS od ON o.ono = od.ono JOIN `product` AS p ON od.pno = p.pno JOIN `productimg` AS ip on p.pno = ip.pno
p.deldate IS NULL AND p.pname like CONCAT('%','${keyword}','%') AND p.company like CONCAT('%','${keyword}','%') AND p.seller like CONCAT('%','${keyword}','%') AND o.uid='${keyword}'
)AS line FROM `orders` AS o JOIN `orderdetail` AS od ON o.ono = od.ono JOIN `product` AS p ON od.pno = p.pno JOIN `productimg` AS ip on p.pno = ip.pno
p.deldate IS NULL AND p.pname like CONCAT('%','${keyword}','%') AND p.company like CONCAT('%','${keyword}','%') AND p.seller like CONCAT('%','${keyword}','%') AND o.uid='${keyword}'
GROUP BY o.ono LIMIT ${size} OFFSET ${(pg - 1) * size}
